import React, { Component, useEffect, useState } from 'react';
import axios from 'axios';
import './Carometro.css';
import Main from '../template/Main';
import '../CrudCurso/CrudCurso.js';


const title = "Cadastro de Alunos";
const urlCurso = "http://localhost:5120/api/curso";
const urlAPI = "http://localhost:5120/api/aluno";
const initialState = {
    aluno: { id: 0, ra: '', nome: '', codCurso: 0 },
    lista: [],
    listaCurso: [],
    listaFiltro: []
}



export default class Carometro extends Component {

    state = {
        ...initialState

    }

    componentDidMount() {
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
            console.log(resp.data)
        })
        axios(urlCurso).then(response => {
            this.setState({ listaCurso: response.data })
            console.log(response.data);
        })


    }


    getListaAtualizada(aluno, add = true, remove = false) {
        const lista = this.state.lista.filter(a => a.id !== aluno.id);
        if (add) lista.unshift(aluno);
        if (remove) lista.splice(aluno);
        return lista;
    }


    atualizaCampo(event) {
        
        
        //clonar usuário a partir do state, para não alterar o state diretamente
        const aluno = { ...this.state.aluno };
        //usar o atributo NAME do input identificar o campo a ser atualizado
        aluno[event.target.name] = event.target.value;
        //atualizar o state
        this.setState({ aluno });
    }

    carregar(aluno) {
        this.setState({ aluno })
    }

    filtroCurso(event){
        const listaFiltro = this.state.listaCurso.filter(a => a.codCurso !== aluno.codCurso);
        return listaFiltro
    }

    renderForm() {
        return (
            <div className="inclui-container">

                <label> Código do Curso: </label>
                <select
                    id="codCurso"
                    name="codCurso"
                    className="form-input"
                    value={this.state.aluno.codCurso}
                    onChange={e => this.atualizaCampo(e)}
                    placeholder="Selecione o curso"
                >
                    <option value="0">Selecione uma opção</option>
                    {this.state.listaCurso.map(curso => (<option key={curso.codCurso} value={curso.codCurso}>{curso.codCurso} - {curso.nomeCurso}</option>))}
                </select>
            </div>
        )
    }

    renderTable() {
        return (
            <div className="listagem">
                <table className="listaAlunos" id="tblListaAlunos">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloRa">Ra</th>
                            <th className="tabTituloNome">Nome</th>
                            <th className="tabTituloCurso">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        <div className="inclui-container">

                            <label> Código do Curso: </label>
                            <select
                                id="codCurso"
                                name="codCurso"
                                className="form-input"
                                value={this.filtroCurso.e}
                                onChange={e => this.filtroCurso(e)}
                                placeholder="Selecione o curso"
                            >
                                <option value="0">Selecione uma opção</option>
                                {this.state.listaCurso.map(curso => (<option key={curso.codCurso} value={curso.codCurso}>{curso.codCurso} - {curso.nomeCurso}</option>))}
                            </select>
                        </div>
                        
                       {this.state.listaFiltro.map(
                            (aluno) =>
                                <tr key={aluno.codCurso}>
                                    <td>{aluno.ra}</td>
                                    <td>{aluno.nome}</td>
                                    <td>{aluno.codCurso}</td>
                                </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }

    render() {
        return (
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>

        )
    }


}
