import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Main from "../template/Main";
import Cards from "../template/Card";
import "./Showroom.css";

export default function Showroom() {


    const title = "Showroom";
    const [cursos, setCursos] = useState([]);
    const [alunos, setAlunos] = useState([]);
    const [inputCurso, setInputCurso] = useState([]);

    const urlMontadora = "http://localhost:5120/api/montadora";
    const urlVeiculo = "http://localhost:5120/api/veiculo";
    const urlImg =   "http://xsgames.co/randomusers/assets/avatars/pixel/";

    useEffect(() => {
        axios(urlAluno).then((reponse) => {
            setAlunos(
                reponse.data.map((veiculo) => ({
                    id: veiculo.id,
                    modelo: veiculo.modelo,
                    ano: veiculo.ano,
                    cor: veiculo.cor,
                    km: veiculo.km,
                    placa:veiculo.placa,
                }))
            );
        });
    }, []);

    useEffect(() => {
        axios(urlCurso).then((reponse) => {
            setCursos(
                reponse.data.map((montadora) => ({
                    id: montadora.id,
                    montadora: montadora.montadora,
                    montadora: montadora.pais,
                    
                }))
            );
        });
    }, []);

    const atualizaCurso = (codCurso) => {
        const curso = cursos.find((curso) => String(curso.codCurso) === codCurso);

        setInputCurso(curso);
    };

    const selecionaAlunos = (alunos) => {
        if (inputCurso) {
            return alunos.filter((aluno) => aluno.codCurso === inputCurso.codCurso);
        }

        return alunos;
    };

    return (
        <Main title={title}>
            <div>
                <div>
                    <select
                        className="select"
                        onChange={(event) => atualizaCurso(event.target.value)}
                        value={
                            inputCurso
                                ? cursos.find(
                                    (curso) => curso.nomeCurso === inputCurso.nomeCurso
                                )?.codCurso : ""
                        }
                    >
                        <option value="">
                            Todos
                        </option>
                        {cursos.map((curso) => (
                            <option value={curso.codCurso} key={curso.codCurso}>
                                {curso.nomeCurso}
                            </option>
                        ))}
                    </select>
                    <p></p> {/* Coloquei essa tag p para separar o card do bot√£o de selecionar*/}
                </div>
                {selecionaAlunos(alunos).map((aluno) => (
                    <Cards
                        codCurso={aluno.codCurso}
                        nome={aluno.nome}
                        ra={aluno.ra}
                        key={aluno.ra}
                        img={`${urlImg}${aluno.id}.jpg`}
                    />
                ))}
            </div>
            
        </Main>
        
    );
}
